mapper.select:
 select p1.ATTR_ID
from mkt_cam_chl_conf_attr p1 LEFT JOIN mkt_cam_chl_conf p2 on p1.EVT_CONTACT_CONF_ID = p2.EVT_CONTACT_CONF_ID
WHERE p2.MKT_CAMPAIGN_ID = #{campaignId}

mapper.update:
update mkt_cam_chl_conf_attr




        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.attrId}






        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.attrValueId}







        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.attrValue}







        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.statusCd}







        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.createStaff}







        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.updateStaff}







        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.createDate}







        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.updateDate}







        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.statusDate}







        WHEN  EVT_CONTACT_CONF_ID = #{item.evtContactConfId} THEN #{item.remark}







    EVT_CONTACT_CONF_ID = #{item.evtContactConfId}
