mapper.select:
select MKT_CAM_ITEM_ID, MKT_CAMPAIGN_ID, ITEM_TYPE, ITEM_ID, PRIORITY, ITEM_GROUP, OFFER_CODE,OFFER_NAME, STATUS_CD, STATUS_DATE, CREATE_STAFF, CREATE_DATE, UPDATE_STAFF, UPDATE_DATE, REMARK, LAN_ID from mkt_cam_item where 1=1
and MKT_CAMPAIGN_ID = #{campaignId}
and ITEM_TYPE = #{type}
and OFFER_NAME like CONCAT('%',#{name,jdbcType=VARCHAR},'%')
