mapper.select:
select MKT_CAMPAIGN_ID, INIT_ID, TIGGER_TYPE, MKT_CAMPAIGN_NAME, PLAN_BEGIN_TIME, PLAN_END_TIME,
MKT_CAMPAIGN_TYPE,  MANAGE_TYPE, MKT_ACTIVITY_NBR,  STATUS_CD, LAN_ID, CREATE_CHANNEL, CREATE_DATE,CREATE_STAFF, LAN_ID_FOUR, LAN_ID_FIVE,
(
SELECT
count(*)
FROM
mkt_campaign_rel r
WHERE
(
r.A_MKT_CAMPAIGN_ID = t.MKT_CAMPAIGN_ID
AND r.STATUS_CD = 1000
AND r.REL_TYPE = 1000
)
OR (
r.Z_MKT_CAMPAIGN_ID = t.MKT_CAMPAIGN_ID
AND r.STATUS_CD = 1000
AND r.REL_TYPE = 1000
)
) AS REL_COUNT,
(
SELECT
r.A_MKT_CAMPAIGN_ID
FROM
mkt_campaign_rel r
WHERE
t.MKT_CAMPAIGN_ID = r.Z_MKT_CAMPAIGN_ID
) AS A_MKT_CAMPAIGN_ID
from mkt_campaign t
where 1 = 1

    AND t.MKT_CAMPAIGN_NAME like CONCAT('%',#{mktCampaignName,jdbcType=VARCHAR},'%')


    AND t.STATUS_CD IN ${statusCd}


    AND t.TIGGER_TYPE = #{tiggerType,jdbcType=VARCHAR}


    AND t.MANAGE_TYPE = #{mktCampaignCategory,jdbcType=VARCHAR}


    AND t.MKT_CAMPAIGN_TYPE = #{mktCampaignType,jdbcType=VARCHAR}


    AND t.LAN_ID = #{lanId,jdbcType=BIGINT}


    AND t.CREATE_CHANNEL = #{createChannel,jdbcType=VARCHAR}


    AND t.CREATE_STAFF = #{createStaff,jdbcType=BIGINT}


    AND t.LAN_ID_FOUR = #{lanIdFour,jdbcType=BIGINT}


    AND t.LAN_ID_FIVE = #{lanIdFive,jdbcType=BIGINT}

order by t.UPDATE_DATE desc

