mapper.select:
select MKT_CAMPAIGN_ID, INIT_ID, TIGGER_TYPE, MKT_CAMPAIGN_NAME, PLAN_BEGIN_TIME, PLAN_END_TIME,
BEGIN_TIME, END_TIME, MKT_CAMPAIGN_TYPE, MANAGE_TYPE, MKT_ACTIVITY_NBR, MKT_ACTIVITY_TARGET, MKT_CAMPAIGN_DESC,
CALC_DISPLAY, ISALE_DISPLAY, EXEC_TYPE, EXEC_INVL, EXEC_NUM, STATUS_CD, STATUS_DATE, CREATE_STAFF, CREATE_DATE,
UPDATE_STAFF, UPDATE_DATE, REMARK, LAN_ID, CREATE_CHANNEL, DIRECTORY_ID, CAM_LEVEL, IS_CHECK_RULE, SRC_ID, SRC_TYPE,
SERVICE_CANCLE_FLAG, REGION_ID, LIFE_STAGE, SERVICE_TYPE, EXT_MKT_CAMPAIGN_ID,EXEC_INIT_TIME, LAN_ID_FOUR, LAN_ID_FIVE
from mkt_campaign
where 1 = 1

    AND MKT_CAMPAIGN_ID = #{mktCampaignId,jdbcType=BIGINT}


    AND INIT_ID = #{initId,jdbcType=BIGINT}



    AND MKT_CAMPAIGN_NAME like CONCAT('%',#{mktCampaignName,jdbcType=VARCHAR},'%')


    AND STATUS_CD IN ${statusCd}


    AND TIGGER_TYPE = #{tiggerType,jdbcType=VARCHAR}


    AND MKT_CAMPAIGN_TYPE = #{mktCampaignType,jdbcType=VARCHAR}


    AND PLAN_BEGIN_TIME = #{planBeginTime,jdbcType=DATE}


    AND PLAN_END_TIME = #{planEndTime,jdbcType=DATE}


    AND MANAGE_TYPE = #{mktCampaignCategory,jdbcType=VARCHAR}


    AND MKT_ACTIVITY_NBR = #{mktActivityNbr,jdbcType=VARCHAR}


    AND MKT_ACTIVITY_TARGET like CONCAT('%',#{mktActivityTarget,jdbcType=VARCHAR},'%')


    AND mktCampaignDesc like CONCAT('%',#{mktCampaignDesc,jdbcType=VARCHAR},'%')


    AND CALC_DISPLAY = #{calcDisplay,jdbcType=BIGINT}


    AND ISALE_DISPLAY = #{isaleDisplay,jdbcType=BIGINT}


    AND STATUS_DATE = #{statusDate,jdbcType=TIMESTAMP}


    AND CREATE_STAFF = #{createStaff,jdbcType=BIGINT}


    AND CREATE_DATE = #{statusCd,jdbcType=TIMESTAMP}


    AND UPDATE_STAFF = #{updateStaff,jdbcType=BIGINT}


    AND UPDATE_DATE = #{statusCd,jdbcType=TIMESTAMP}


    AND CREATE_CHANNEL = #{createChannel,jdbcType=VARCHAR}


    AND DIRECTORY_ID = #{directoryId,jdbcType=BIGINT}


    AND CAM_LEVEL = #{camLevel,jdbcType=BIGINT}


    AND IS_CHECK_RULE = #{isCheckRule,jdbcType=VARCHAR}


    AND SRC_ID #{srcId,jdbcType=VARCHAR},


    AND SRC_TYPE = #{srcType,jdbcType=VARCHAR},


    AND SERVICE_CANCLE_FLAG = #{serviceCancleFlag,jdbcType=BIGINT},


    AND REGION_ID = #{regionId,jdbcType=BIGINT},


    AND LIFE_STAGE =  #{lifeStage,jdbcType=VARCHAR}


    AND SERVICE_TYPE =  #{serviceType,jdbcType=VARCHAR}


    AND EXT_MKT_CAMPAIGN_ID =  #{extMktCampaignId,jdbcType=VARCHAR}


    AND EXEC_INIT_TIME =  #{execInitTime,jdbcType=VARCHAR}


    AND t.LAN_ID_FOUR = #{lanIdFour,jdbcType=BIGINT}


    AND t.LAN_ID_FIVE = #{lanIdFive,jdbcType=BIGINT}

order by UPDATE_DATE desc

select MKT_CAMPAIGN_ID, INIT_ID, TIGGER_TYPE, MKT_CAMPAIGN_NAME, PLAN_BEGIN_TIME, PLAN_END_TIME, BEGIN_TIME, END_TIME, MKT_CAMPAIGN_TYPE, MANAGE_TYPE, MKT_ACTIVITY_NBR, MKT_ACTIVITY_TARGET, MKT_CAMPAIGN_DESC, CALC_DISPLAY, ISALE_DISPLAY, EXEC_TYPE, EXEC_INVL, EXEC_NUM, STATUS_CD, STATUS_DATE, CREATE_STAFF, CREATE_DATE, UPDATE_STAFF, UPDATE_DATE, REMARK, LAN_ID, CREATE_CHANNEL, DIRECTORY_ID, CAM_LEVEL, IS_CHECK_RULE, SRC_ID, SRC_TYPE, SERVICE_CANCLE_FLAG, REGION_ID, LIFE_STAGE, SERVICE_TYPE, EXT_MKT_CAMPAIGN_ID, EXEC_INIT_TIME, LAN_ID_FOUR, LAN_ID_FIVE from mkt_campaign where 1 = 1
AND EXEC_TYPE = #{execType,jdbcType=VARCHAR}
AND STATUS_CD = #{statusCd,jdbcType=VARCHAR}
order by UPDATE_DATE desc

select MKT_CAMPAIGN_ID, INIT_ID, TIGGER_TYPE, MKT_CAMPAIGN_NAME, PLAN_BEGIN_TIME, PLAN_END_TIME, BEGIN_TIME, END_TIME, MKT_CAMPAIGN_TYPE, MANAGE_TYPE, MKT_ACTIVITY_NBR, MKT_ACTIVITY_TARGET, MKT_CAMPAIGN_DESC, CALC_DISPLAY, ISALE_DISPLAY, EXEC_TYPE, EXEC_INVL, EXEC_NUM, STATUS_CD, STATUS_DATE, CREATE_STAFF, CREATE_DATE, UPDATE_STAFF, UPDATE_DATE, REMARK, LAN_ID, CREATE_CHANNEL, DIRECTORY_ID, CAM_LEVEL, IS_CHECK_RULE, SRC_ID, SRC_TYPE, SERVICE_CANCLE_FLAG, REGION_ID, LIFE_STAGE, SERVICE_TYPE, EXT_MKT_CAMPAIGN_ID,EXEC_INIT_TIME,INIT_ID from mkt_campaign where 1 = 1
AND MKT_CAMPAIGN_ID = #{mktCampaignId}
AND STATUS_CD = #{statusCd}
AND STATUS_CD IN (2002,2008)
AND MKT_CAMPAIGN_TYPE = #{mktCampaignType}
AND MKT_CAMPAIGN_TYPE IN (1000,2000,3000,4000,5000,6000)
AND MKT_CAMPAIGN_NAME LIKE CONCAT('%',#{mktCampaignName},'%')
AND LAN_ID IN #{lanId}
ORDER BY CREATE_DATE DESC

