mapper.select:
select MKT_CAMPAIGN_ID, TIGGER_TYPE, MKT_CAMPAIGN_NAME, PLAN_BEGIN_TIME, PLAN_END_TIME, MKT_CAMPAIGN_TYPE, MANAGE_TYPE, MKT_ACTIVITY_NBR, STATUS_CD, LAN_ID, CREATE_CHANNEL, CREATE_DATE,CREATE_STAFF, ( SELECT count(*) FROM mkt_campaign_rel r WHERE ( r.A_MKT_CAMPAIGN_ID = t.MKT_CAMPAIGN_ID AND r.STATUS_CD = 1000 AND r.REL_TYPE = 1000 ) OR ( r.Z_MKT_CAMPAIGN_ID = t.MKT_CAMPAIGN_ID AND r.STATUS_CD = 1000 AND r.REL_TYPE = 1000 ) ) AS REL_COUNT, ( SELECT r.A_MKT_CAMPAIGN_ID FROM mkt_campaign_rel r WHERE t.MKT_CAMPAIGN_ID = r.Z_MKT_CAMPAIGN_ID ) AS A_MKT_CAMPAIGN_ID from mkt_campaign t where 1 = 1
AND t.MKT_CAMPAIGN_NAME like CONCAT('%',#{mktCampaignName,jdbcType=VARCHAR},'%')
AND t.STATUS_CD = #{statusCd,jdbcType=VARCHAR}
AND t.TIGGER_TYPE = #{tiggerType,jdbcType=VARCHAR}
AND t.MANAGE_TYPE = #{mktCampaignCategory,jdbcType=VARCHAR}
AND t.MKT_CAMPAIGN_TYPE = #{mktCampaignType,jdbcType=VARCHAR}
AND t.LAN_ID = #{lanId,jdbcType=BIGINT}
AND t.CREATE_CHANNEL = #{createChannel,jdbcType=VARCHAR}
AND t.CREATE_STAFF = #{createStaff,jdbcType=BIGINT}
order by t.UPDATE_DATE desc

mapper.update:
update mkt_campaign
set STATUS_CD = #{statusCd,jdbcType=VARCHAR},
UPDATE_STAFF = #{updateStaff,jdbcType=BIGINT},
UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
where MKT_CAMPAIGN_ID = #{mktCampaignId,jdbcType=BIGINT}

mapper.select:
select MKT_CAMPAIGN_ID, TIGGER_TYPE, MKT_CAMPAIGN_NAME, PLAN_BEGIN_TIME, PLAN_END_TIME,
BEGIN_TIME, END_TIME, MKT_CAMPAIGN_TYPE, MANAGE_TYPE, MKT_ACTIVITY_NBR, MKT_ACTIVITY_TARGET, MKT_CAMPAIGN_DESC,
CALC_DISPLAY, ISALE_DISPLAY, EXEC_TYPE, EXEC_INVL, EXEC_NUM, STATUS_CD, STATUS_DATE, CREATE_STAFF, CREATE_DATE,
UPDATE_STAFF, UPDATE_DATE, REMARK, LAN_ID, CREATE_CHANNEL, DIRECTORY_ID, CAM_LEVEL, IS_CHECK_RULE, SRC_ID, SRC_TYPE,
SERVICE_CANCLE_FLAG, REGION_ID, LIFE_STAGE, SERVICE_TYPE, EXT_MKT_CAMPAIGN_ID,EXEC_INIT_TIME
from mkt_campaign
where 1 = 1

    AND MKT_CAMPAIGN_ID = #{mktCampaignId,jdbcType=BIGINT}


    AND MKT_CAMPAIGN_NAME like CONCAT('%',#{mktCampaignName,jdbcType=VARCHAR},'%')


    AND STATUS_CD = #{statusCd,jdbcType=VARCHAR}


    AND TIGGER_TYPE = #{tiggerType,jdbcType=VARCHAR}


    AND MKT_CAMPAIGN_TYPE = #{mktCampaignType,jdbcType=VARCHAR}


    AND PLAN_BEGIN_TIME = #{planBeginTime,jdbcType=DATE}


    AND PLAN_END_TIME = #{PLAN_END_TIME,jdbcType=DATE}


    AND MANAGE_TYPE = #{mktCampaignCategory,jdbcType=VARCHAR}


    AND MKT_ACTIVITY_NBR = #{mktActivityNbr,jdbcType=VARCHAR}


    AND MKT_ACTIVITY_TARGET like CONCAT('%',#{mktActivityTarget,jdbcType=VARCHAR},'%')


    AND mktCampaignDesc like CONCAT('%',#{mktCampaignDesc,jdbcType=VARCHAR},'%')


    AND CALC_DISPLAY = #{calcDisplay,jdbcType=BIGINT}


    AND ISALE_DISPLAY = #{isaleDisplay,jdbcType=BIGINT}


    AND STATUS_DATE = #{statusDate,jdbcType=TIMESTAMP}


    AND CREATE_STAFF = #{createStaff,jdbcType=BIGINT}


    AND CREATE_DATE = #{statusCd,jdbcType=TIMESTAMP}


    AND UPDATE_STAFF = #{updateStaff,jdbcType=BIGINT}


    AND UPDATE_DATE = #{statusCd,jdbcType=TIMESTAMP}


    AND CREATE_CHANNEL = #{createChannel,jdbcType=VARCHAR}


    AND DIRECTORY_ID = #{directoryId,jdbcType=BIGINT}


    AND CAM_LEVEL = #{camLevel,jdbcType=BIGINT}


    AND IS_CHECK_RULE = #{isCheckRule,jdbcType=VARCHAR}


    AND SRC_ID #{srcId,jdbcType=VARCHAR},


    AND SRC_TYPE = #{srcType,jdbcType=VARCHAR},


    AND SERVICE_CANCLE_FLAG = #{serviceCancleFlag,jdbcType=BIGINT},


    AND REGION_ID = #{regionId,jdbcType=BIGINT},


    AND LIFE_STAGE =  #{lifeStage,jdbcType=VARCHAR}


    AND SERVICE_TYPE =  #{serviceType,jdbcType=VARCHAR}


    AND EXT_MKT_CAMPAIGN_ID =  #{extMktCampaignId,jdbcType=VARCHAR}


    AND EXEC_INIT_TIME =  #{execInitTime,jdbcType=BIGINT}

order by UPDATE_DATE desc

select MKT_CAMPAIGN_ID, TIGGER_TYPE, MKT_CAMPAIGN_NAME, PLAN_BEGIN_TIME, PLAN_END_TIME, BEGIN_TIME, END_TIME, MKT_CAMPAIGN_TYPE, MANAGE_TYPE, MKT_ACTIVITY_NBR, MKT_ACTIVITY_TARGET, MKT_CAMPAIGN_DESC, CALC_DISPLAY, ISALE_DISPLAY, EXEC_TYPE, EXEC_INVL, EXEC_NUM, STATUS_CD, STATUS_DATE, CREATE_STAFF, CREATE_DATE, UPDATE_STAFF, UPDATE_DATE, REMARK, LAN_ID, CREATE_CHANNEL, DIRECTORY_ID, CAM_LEVEL, IS_CHECK_RULE, SRC_ID, SRC_TYPE, SERVICE_CANCLE_FLAG, REGION_ID, LIFE_STAGE, SERVICE_TYPE, EXT_MKT_CAMPAIGN_ID, EXEC_INIT_TIME from mkt_campaign where 1 = 1
AND EXEC_TYPE = #{execType,jdbcType=VARCHAR}
AND STATUS_CD = #{statusCd,jdbcType=VARCHAR}
order by UPDATE_DATE desc
