mapper.select:
SELECT COUNT (1)
from mkt_campaign
where 1 = 1

    AND PLAN_BEGIN_TIME > #{map.startDate,jdbcType=TIMESTAMP}


    AND PLAN_END_TIME > #{map.endDate,jdbcType=TIMESTAMP}


    AND STATUS_CD IN ${map.statusCd}


    AND LAN_ID = #{map.lanId,jdbcType=BIGINT}


    AND MKT_CAMPAIGN_TYPE = #{map.mktCampaignType}


    AND TIGGER_TYPE = #{map.tiggerType,jdbcType=VARCHAR}


    AND ONE_CHANNEL_FLG = #{map.oneChannelFlg}

SELECT COUNT (1)
from mkt_campaign
where  #{map.endDate,jdbcType=TIMESTAMP} > PLAN_END_TIME
AND PLAN_BEGIN_TIME > #{map.startDate,jdbcType=TIMESTAMP}
AND PLAN_END_TIME > #{map.startDate,jdbcType=TIMESTAMP}

    AND STATUS_CD IN ${map.statusCd}


    AND LAN_ID = #{map.lanId,jdbcType=BIGINT}


    AND MKT_CAMPAIGN_TYPE = #{map.mktCampaignType}


    AND TIGGER_TYPE = #{map.tiggerType,jdbcType=VARCHAR}


    AND ONE_CHANNEL_FLG = #{map.oneChannelFlg}

SELECT t.MKT_CAMPAIGN_ID, t.INIT_ID, t.TIGGER_TYPE, t.MKT_CAMPAIGN_NAME,
t.PLAN_BEGIN_TIME, t.PLAN_END_TIME, t.BEGIN_TIME,
t.END_TIME, t.MKT_CAMPAIGN_TYPE, t.MANAGE_TYPE,
t.MKT_ACTIVITY_NBR, t.MKT_ACTIVITY_TARGET, t.MKT_CAMPAIGN_DESC,
t.CALC_DISPLAY, t.ISALE_DISPLAY,
t.EXEC_TYPE, t.EXEC_INVL, t.EXEC_NUM,
t.STATUS_CD, t.STATUS_DATE, t.CREATE_STAFF,
t.CREATE_DATE, t.UPDATE_STAFF, t.UPDATE_DATE,
t.REMARK, t.LAN_ID, t.CREATE_CHANNEL, t.DIRECTORY_ID,
t.CAM_LEVEL, t.IS_CHECK_RULE, t.SRC_TYPE, t.SRC_ID,
t.SERVICE_CANCLE_FLAG, t.REGION_ID, t.LIFE_STAGE, t.SERVICE_TYPE, t.EXT_MKT_CAMPAIGN_ID, t.EXEC_INIT_TIME, t.LAN_ID_FOUR, t.LAN_ID_FIVE, t.ONE_CHANNEL_FLG,
t.THE_ME, t.REGION_FLG
from mkt_campaign t
where 1 = 1

    AND PLAN_BEGIN_TIME > #{map.startDate,jdbcType=TIMESTAMP}


    AND PLAN_END_TIME > #{map.endDate,jdbcType=TIMESTAMP}


    AND STATUS_CD IN ${map.statusCd}


    AND LAN_ID = #{map.lanId,jdbcType=BIGINT}


    AND MKT_CAMPAIGN_TYPE = #{map.mktCampaignType}


    AND TIGGER_TYPE = #{map.tiggerType,jdbcType=VARCHAR}


    AND ONE_CHANNEL_FLG = #{map.oneChannelFlg}

SELECT t.MKT_CAMPAIGN_ID, t.INIT_ID, t.TIGGER_TYPE, t.MKT_CAMPAIGN_NAME, t.PLAN_BEGIN_TIME, t.PLAN_END_TIME, t.BEGIN_TIME, t.END_TIME, t.MKT_CAMPAIGN_TYPE, t.MANAGE_TYPE, t.MKT_ACTIVITY_NBR, t.MKT_ACTIVITY_TARGET, t.MKT_CAMPAIGN_DESC, t.CALC_DISPLAY, t.ISALE_DISPLAY, t.EXEC_TYPE, t.EXEC_INVL, t.EXEC_NUM, t.STATUS_CD, t.STATUS_DATE, t.CREATE_STAFF, t.CREATE_DATE, t.UPDATE_STAFF, t.UPDATE_DATE, t.REMARK, t.LAN_ID, t.CREATE_CHANNEL, t.DIRECTORY_ID, t.CAM_LEVEL, t.IS_CHECK_RULE, t.SRC_TYPE, t.SRC_ID, t.SERVICE_CANCLE_FLAG, t.REGION_ID, t.LIFE_STAGE, t.SERVICE_TYPE, t.EXT_MKT_CAMPAIGN_ID, t.EXEC_INIT_TIME, t.LAN_ID_FOUR, t.LAN_ID_FIVE, t.ONE_CHANNEL_FLG, t.THE_ME, t.REGION_FLG from mkt_campaign t where 1 = 1
AND PLAN_END_TIME BETWEEN #{map.startDate,jdbcType=TIMESTAMP} AND #{map.endDate,jdbcType=TIMESTAMP}
AND t.STATUS_CD IN ${map.statusCd}
AND t.LAN_ID = #{map.lanId,jdbcType=BIGINT}
AND t.MKT_CAMPAIGN_TYPE IN ${map.mktCampaignType}
AND t.TIGGER_TYPE = #{map.tiggerType,jdbcType=VARCHAR}
AND t.MKT_CAMPAIGN_ID IN ( SELECT tril.CAMPAIGN_ID FROM trial_operation tril WHERE tril.CREATE_STAFF = 1000)
AND t.MKT_CAMPAIGN_ID NOT IN ( SELECT tril.CAMPAIGN_ID FROM trial_operation tril WHERE tril.CREATE_STAFF = 1000 )

select MKT_CAMPAIGN_ID campaignId, MKT_CAMPAIGN_NAME campaignName,
from mkt_campaign
where SRC_TYPE = #{map.serviceType}
and MKT_CAMPAIGN_TYPE = #{map.mktCampaignType}
AND STATUS_CD IN (2002, 2006, 2007, 2008, 2010)
group by INIT_ID
