mapper.select:
select OPERATION_ID, MKT_CAMPAIGN_ID, MKT_ACTIVITY_NBR, MKT_CAMPAIGN_NAME, MKT_CAMPAIGN_STATE_BEFORE, MKT_CAMPAIGN_STATE_AFTER, OPERATOR_ACCOUNT, OPERATION_TYPE, OPERATION_DATE from mkt_operator_log where 1=1
AND MKT_ACTIVITY_NBR = #{mktActivityNbr,jdbcType=VARCHAR}
AND MKT_CAMPAIGN_NAME like CONCAT('%',#{mktCampaignName,jdbcType=VARCHAR},'%')
AND OPERATOR_ACCOUNT = #{operatorAccount,jdbcType=VARCHAR}
