mapper.select:
select * from event

where 1=1

    and id = #{id}


    and name = #{name}

mapper.update:
update menu

    name = #{name}
    remark = #{remark}
    create_datetime = #{createDate}
    update_datetime = #{updateDate}

where id = #{id}

update event
set STATUS_CD = #{statusCd}
where EVENT_ID = #{contactEvtId,jdbcType=BIGINT}

update event
set EVENT_NBR = #{contactEvtCode}
where EVENT_ID = #{contactEvtId,jdbcType=BIGINT}

mapper.select:
select e.EVENT_ID contactEvtId, e.EVENT_NBR contactEvtCode, e.EVENT_NAME contactEvtName, e.EVENT_DESC contactEvtDesc, e.EVT_MAPPED_ADDR evtMappedAddr, e.EVT_MAPPED_IP evtMappedIp, e.EVT_PROCOTOL_TYPE evtProcotolType, e.EVT_MAPPED_FUN_NAME evtMappedFunName, e.MKT_CAMPAIGN_TYPE mktCampaignType, t.EVT_TYPE_NAME contactEvtTypeName, e.EVENT_TRIG_TYPE evtTrigType, e.STATUS_CD statusCd from event e
where 1=1
and e.EVENT_ID = #{contactEvtId}
and e.EVENT_NBR = #{contactEvtCode}
and e.EVENT_NAME LIKE CONCAT(CONCAT('%',#{contactEvtName}),'%')
and e.EVT_TYPE_ID = #{contactEvtTypeId}
and e.STATUS_CD = #{statusCd}
ORDER BY e.CREATE_DATE DESC

select * from event
where EVENT_NBR IN

    #{item}

select EVT_INTERFACE_REL_ID evtInterfaceRelId, EVENT_ID evtId, INTERFACE_ID interfaceId, CHANNEL_CODE channelCode
from mkt_evt_interface_rel
where EVENT_ID = #{evtId,jdbcType=BIGINT}
