mapper.select:
select * from event

where 1=1

    and id = #{id}


    and name = #{name}

mapper.update:
update menu

    name = #{name}
    remark = #{remark}
    create_datetime = #{createDate}
    update_datetime = #{updateDate}

where id = #{id}

update event
set STATUS_CD = #{statusCd}
where EVENT_ID = #{eventId,jdbcType=BIGINT}
