mapper.select:
select c.INTERFACE_CFG_ID interfaceCfgId , c.EVT_SRC_ID evtSrcId, c.INTERFACE_NAME interfaceName, c.INTERFACE_DESC interfaceDesc, c.INTERFACE_NBR interfaceNbr, c.INTERFACE_TYPE interfaceType, c.PROVIDER provider, c.CALLER caller, c.PROTOCOL_TYPE protocolType, c.STATUS_CD statusCd, c.CREATE_STAFF createStaff, c.UPDATE_STAFF updateStaff, c.REMARK remark, s.EVT_SRC_NAME evtSrcName from interface_cfg c
where 1 = 1
and s.EVT_SRC_ID = #{evtSrcId}
and c.INTERFACE_NAME LIKE CONCAT(CONCAT('%',#{interfaceName}),'%')
and s.INTERFACE_TYPE = #{interfaceType}
