mapper.update:
update close_rule set CLOSE_NAME = #{closeName,jdbcType=VARCHAR}, CLOSE_TYPE = #{closeType,jdbcType=VARCHAR}, PRODUCT_TYPE = #{productType,jdbcType=VARCHAR}, CHANNEL_CONTACTS = #{channelContacts,jdbcType=VARCHAR}, EFFECTIVE_DATE = #{effectiveDate,jdbcType=TIMESTAMP}, FAILURE_DATE = #{failureDate,jdbcType=TIMESTAMP}, LABEL_CODE = #{labelCode,jdbcType=VARCHAR}, CHOOSE_PRODUCT = #{chooseProduct,jdbcType=VARCHAR},
AND EXPRESSION = #{expression,jdbcType=VARCHAR}
-- EXPRESSION = #{expression,jdbcType=VARCHAR}, DAY_START = #{dayStart,jdbcType=TIMESTAMP}, DAY_END = #{dayEnd,jdbcType=TIMESTAMP}, STATUS_CD = #{statusCd,jdbcType=VARCHAR}, CREATE_STAFF = #{createStaff,jdbcType=BIGINT}, UPDATE_STAFF = #{updateStaff,jdbcType=BIGINT}, CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}, STATUS_DATE = #{statusDate,jdbcType=TIMESTAMP}, UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}, REMARK = #{remark,jdbcType=VARCHAR}, USER_LIST = #{userList,jdbcType=VARCHAR}, CONDITION_ID = #{conditionId,jdbcType=BIGINT}, DAYS = #{days,jdbcType=VARCHAR}, TIMES = #{times,jdbcType=VARCHAR}, OFFER_INFO = #{offerInfo,jdbcType=VARCHAR}, CLOSE_CODE = #{closeCode,jdbcType=VARCHAR}, NOTE_ONE = #{noteOne,jdbcType=VARCHAR}, NOTE_TWO = #{noteTwo,jdbcType=VARCHAR}, NOTE_THREE = #{noteThree,jdbcType=VARCHAR} where RULE_ID = #{ruleId,jdbcType=BIGINT}

mapper.select:
select RULE_ID, CLOSE_NAME, CLOSE_TYPE, PRODUCT_TYPE, CHANNEL_CONTACTS, EFFECTIVE_DATE, FAILURE_DATE,LABEL_CODE,CHOOSE_PRODUCT, EXPRESSION,DAY_START,DAY_END,STATUS_CD, CREATE_STAFF, UPDATE_STAFF, CREATE_DATE, STATUS_DATE, UPDATE_DATE, REMARK, USER_LIST, CONDITION_ID, DAYS, TIMES, OFFER_INFO,CLOSE_CODE,NOTE_ONE,NOTE_TWO,NOTE_THREE from close_rule where 1 = 1
AND CLOSE_NAME like concat('%',concat(#{closeName},'%'))
AND CLOSE_TYPE = #{closeType}
AND CREATE_STAFF = #{staffId}
ORDER BY RULE_ID DESC
