mapper.select:
select sf.STAFF_ID, sf.STAFF_CODE, sf.STAFF_ACCOUNT, sf.STAFF_NAME, sf.STAFF_PHONE, sf.STAFF_TELEPHONE, sf.STAFF_EMAIL, sf.CHANNEL_ID, sf.CITY_ID, sf.STATUS, sf.LOGIN_STATUS, sf.LAST_LOGIN, sf.CREATE_DATE, sf.CREATE_STAFF, sf.UPDATE_DATE, sf.UPDATE_STAFF, re.ROLE_NAME, cc.CONTACT_CHL_NAME, sa.name, sp.PARAM_NAME from sys_staff as sf left join sys_staff_role as sr on sf.STAFF_ID = sr.STAFF_ID left join sys_role as re on sr.ROLE_ID = re.ROLE_ID left join contact_channel as cc on sf.CHANNEL_ID = cc.CONTACT_CHL_ID left join sys_area as sa on sf.CITY_ID = sa.area_id left join sys_params as sp on sf.LOGIN_STATUS = sp.PARAM_VALUE and sp.PARAM_KEY = "SYSYTEM_LOGIN_STATUS" where 1=1
and sf.STAFF_ACCOUNT like CONCAT('%',#{staffAccount,jdbcType=VARCHAR},'%')
and sf.STAFF_NAME like CONCAT('%',#{staffName,jdbcType=VARCHAR},'%')
and sf.STATUS = #{status,jdbcType=BIGINT}
and sf.CITY_ID = #{cityId,jdbcType=BIGINT}
and sf.CHANNEL_ID = #{channelId,jdbcType=BIGINT}
and sf.LOGIN_STATUS = #{loginStatus,jdbcType=BIGINT}
order by sf.CREATE_DATE desc

mapper.update:
update sys_staff
set STATUS = #{status,jdbcType=BIGINT},
  UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP},
  UPDATE_STAFF = #{updateStaff,jdbcType=BIGINT}
where STAFF_ID = #{staffId,jdbcType=BIGINT}

update sys_staff
set PASSWORD = #{password,jdbcType=BIGINT}
where STAFF_ID = #{staffId,jdbcType=BIGINT}

mapper.select:
select count(0)
from sys_staff
where STAFF_ACCOUNT = #{staffAccount,jdbcType=VARCHAR}

mapper.update:
update sys_staff
set LAST_LOGIN = now()
where STAFF_ACCOUNT = #{staffAccount,jdbcType=VARCHAR}
