mapper.select:
select STAFF_ID, STAFF_CODE, STAFF_NAME, PASSWORD, STAFF_PHONE, STAFF_TELEPHONE, STAFF_EMAIL, CHANNEL_ID, CITY_ID, STATUS, LAST_LOGIN, CREATE_DATE, CREATE_STAFF, UPDATE_DATE, UPDATE_STAFF from sys_staff where STAFF_CODE like CONCAT('%',#{staffCode,jdbcType=VARCHAR},'%') and STAFF_NAME like CONCAT('%',#{staffName,jdbcType=VARCHAR},'%')
and STATUS = #{status,jdbcType=BIGINT}
order by CREATE_DATE desc

mapper.update:
update sys_staff
set STATUS = #{status,jdbcType=BIGINT},
  UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP},
  UPDATE_STAFF = #{updateStaff,jdbcType=BIGINT}
where STAFF_ID = #{staffId,jdbcType=BIGINT}

update sys_staff
set PASSWORD = #{password,jdbcType=BIGINT}
where STAFF_ID = #{staffId,jdbcType=BIGINT}
