mapper.select:
select INTERFACE_CFG_ID, EVT_SRC_ID, INTERFACE_NAME, INTERFACE_DESC, INTERFACE_NBR, INTERFACE_TYPE, PROVIDER, CALLER, PROTOCOL_TYPE, STATUS_CD, CREATE_STAFF, UPDATE_STAFF, CREATE_DATE, STATUS_DATE, UPDATE_DATE, REMARK from interface_cfg where 1=1
and EVT_SRC_ID = #{evtSrcId,jdbcType=BIGINT}
and INTERFACE_NAME LIKE CONCAT(CONCAT('%',#{interfaceName}),'%')
and INTERFACE_TYPE = #{interfaceType,jdbcType=VARCHAR}
