mapper.select:
select QUESTION_ID, QUESTION_NAME, QUESTION_TYPE, QUESTION_DESC, ANSWER_TYPE, DEFAULT_ANSWER, STATUS_CD, STATUS_DATE, CREATE_STAFF, CREATE_DATE, UPDATE_STAFF, UPDATE_DATE, REMARK from mkt_question where 1=1
and QUESTION_NAME LIKE CONCAT(CONCAT('%',#{map.questionName}),'%')
and QUESTION_TYPE = #{map.questionType}
and ANSWER_TYPE = #{map.answerType}
order by CREATE_DATE desc
