mapper.select:
select STAFF_ID, STAFF_CODE,STAFF_ACCOUNT, STAFF_NAME, STAFF_PHONE, STAFF_TELEPHONE,
STAFF_EMAIL, CHANNEL_ID, CITY_ID, STATUS, LAST_LOGIN, CREATE_DATE, CREATE_STAFF,
UPDATE_DATE, UPDATE_STAFF
from sys_staff
where 1=1

    and STAFF_ACCOUNT like CONCAT('%',#{staffAccount,jdbcType=VARCHAR},'%')


    and STAFF_NAME like CONCAT('%',#{staffName,jdbcType=VARCHAR},'%')


    and STATUS = #{status,jdbcType=BIGINT}


    and CITY_ID = #{cityId,jdbcType=BIGINT}


    and CHANNEL_ID = #{channelId,jdbcType=BIGINT}


    and LOGIN_STATUS = #{loginStatus,jdbcType=BIGINT}

order by CREATE_DATE desc

mapper.update:
update sys_staff
set STATUS = #{status,jdbcType=BIGINT},
  UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP},
  UPDATE_STAFF = #{updateStaff,jdbcType=BIGINT}
where STAFF_ID = #{staffId,jdbcType=BIGINT}

update sys_staff
set PASSWORD = #{password,jdbcType=BIGINT}
where STAFF_ID = #{staffId,jdbcType=BIGINT}

mapper.select:
select count(0)
from sys_staff
where STAFF_ACCOUNT = #{staffAccount,jdbcType=VARCHAR}

mapper.update:
update sys_staff
set LAST_LOGIN = now()
where STAFF_ACCOUNT = #{staffAccount,jdbcType=VARCHAR}
